import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      currentItems: [],
      items: [
      {
        id: 1,
        title: 'Стул серый',
        img: 'grey-chair.jpeg',
        desc: 'Lorem ipsum dolor sit amet consectetur adipisicing.',
        category: 'chairs',
        price: '49.99'
      },
      {
        id: 2,
        title: 'Стол',
        img: 'table2.jpeg',
        desc: 'Lorem ipsum dolor sit amet consectetur adipisicing.',
        category: 'table',
        price: '49.99'
      },
      {
        id: 3,
        title: 'Диван',
        img: 'sofa2.avif',
        desc: 'Lorem ipsum dolor sit amet consectetur adipisicing.',
        category: 'light',
        price: '49.99'
      },
      {
        id: 1,
        title: 'Лампа',
        img: 'wall-lamp2.avif',
        desc: 'Lorem ipsum dolor sit amet consectetur adipisicing.',
        category: 'light',
        price: '49.99'
      },
      {
        id: 2,
        title: 'Стул белый',
        img: 'chair-white.webp',
        desc: 'Lorem ipsum dolor sit amet consectetur adipisicing.',
        category: 'chairs',
        price: '49.99'
      },
    ]
    }
    this.state.currentItems = this.state.items
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategoriy = this.chooseCategoriy.bind(this)
  }
  render() {
    return (
    <div className="wrapper">
      <Header orders={this.state.orders} onDelete={this.deleteOrder}/>
      <Categories chooseCategoriy={this.chooseCategoriy}/>
      <Items items={this.state.currentItems} onAdd={this.addToOrder}/>
      <Footer />
    </div>
    )
  };

  chooseCategoriy(category) {
    this.setState({
      currentItems: this.state.currentItems.filter(el => el.category === category)
    })
  }
  deleteOrder(id) {
  this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }
  addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
      if(el.id === item.id)
      isInArray = true
    })
    if(!isInArray)
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
